package processor.pipeline;

import java.util.Scanner;

import processor.Processor;

public class OperandFetch {
	Processor containingProcessor;
	IF_OF_LatchType IF_OF_Latch;
	OF_EX_LatchType OF_EX_Latch;
	ALUnit ALU;
	
	public OperandFetch(Processor containingProcessor, IF_OF_LatchType iF_OF_Latch, OF_EX_LatchType oF_EX_Latch, ALUnit ALU)
	{
		this.containingProcessor = containingProcessor;
		this.IF_OF_Latch = iF_OF_Latch;
		this.OF_EX_Latch = oF_EX_Latch;
		this.ALU = ALU;
	}
	
	public String padString(String str, int leng) {
        String pad = "";
		for (int i = str.length()+1; i <= leng; i++)
            pad += "0";
		System.out.println(pad+str + "The final string");
        return pad+str;
    }
	
	public void performOF()
	{
		if(IF_OF_Latch.isOF_enable())
		{
			//TODO
			// First extract the instruction from the if_of_latch.
			int inst = IF_OF_Latch.getInstruction();
			System.out.println("Instruction is: ");
			System.out.println(inst);
			Scanner in = new Scanner(System.in); 
			  
	        String s = in.nextLine();
	  
	        String biii = in.nextLine();
			
	        String byte_inst = Integer.toBinaryString(inst);
			byte_inst = padString(byte_inst, 32); // Pad the string with zeros to the left to length 32.
			String opcode = byte_inst.substring(0,5); // First five
			opcode = padString(opcode, 32);
			
		    System.out.println("Byte inst is : ");
		    System.out.println(byte_inst);
			System.out.println("opcode is : ");
			System.out.println(opcode);
			System.out.println(Integer.valueOf(opcode, 2));
			s = in.nextLine();
			  
		    biii = in.nextLine();
			
			int OpCode = Integer.valueOf(opcode, 2);
			ALU.setOprand(OpCode);
			
			if (OpCode<=20 && OpCode%2==0) {
				// It is R3 Type.
				int rs1 = Integer.valueOf(byte_inst.substring(5, 10), 2);
				int rs2 = Integer.valueOf(byte_inst.substring(10, 15), 2);
				int rd = Integer.valueOf(byte_inst.substring(15,20), 2);
				OF_EX_Latch.setRs1(rs1);
				OF_EX_Latch.setRs2(rs2);
				OF_EX_Latch.setRd(rd);
				OF_EX_Latch.setIs_imm(false);
				ALU.setIsR3Type(true);	
				ALU.setIsR2IType(false);
				ALU.setIsRIType(false);
			}
			else if ((OpCode%2==1 || OpCode==22 || OpCode==26 || OpCode==28) && OpCode!=29) {
				// This is R2I Type.
				String imm_str = byte_inst.substring(15, 32);
				System.out.println("The imm string is : ");
				System.out.println(imm_str);
				int check = Integer.valueOf(imm_str.substring(0, 1));
				System.out.println(check);
				OF_EX_Latch.setIs_negative(false);
				if (check==1){
					System.out.println("Came");
					OF_EX_Latch.setIs_negative(true);
					System.out.println(OF_EX_Latch.getIs_negative());
				}
				int rs1 = Integer.valueOf(byte_inst.substring(5, 10), 2);
				int rd = Integer.valueOf(byte_inst.substring(10, 15), 2);
				int imm = Integer.valueOf(byte_inst.substring(15, 32), 2);

				System.out.println("The value of imm is in bgt: ");
				System.out.println(imm);
				
				OF_EX_Latch.setRs1(rs1);
				OF_EX_Latch.setImm(imm);
				OF_EX_Latch.setRd(rd);
				OF_EX_Latch.setIs_imm(true);
				ALU.setIsR2IType(true);
				ALU.setIsR3Type(false);
				ALU.setIsRIType(false);
				
			}
			else if (OpCode==24 || OpCode==29) {
				// This is the RI Type.
				String imm_str = byte_inst.substring(10, 32);
				OF_EX_Latch.setIs_negative(false);
				System.out.println("The imm string is : ");
				System.out.println(imm_str);
				int check = Integer.valueOf(imm_str.substring(0, 1));
				System.out.println(check);
				if (check==1){
					System.out.println("Came");
					OF_EX_Latch.setIs_negative(true);
					System.out.println(OF_EX_Latch.getIs_negative());
				}
				int rd = Integer.valueOf(byte_inst.substring(5, 10), 2);
				int imm = Integer.valueOf(byte_inst.substring(10, 32), 2);
				System.out.println("The value of imm is in bgt: ");
				System.out.println(imm);
				
				OF_EX_Latch.setRd(rd);
				OF_EX_Latch.setImm(imm);
				OF_EX_Latch.setIs_imm(true); 
				ALU.setIsRIType(true);
				ALU.setIsR2IType(false);
				ALU.setIsR3Type(false);
			}
			else {
				//throw an exception here!.........................................................................................................................
			}
			IF_OF_Latch.setOF_enable(false);
			OF_EX_Latch.setEX_enable(true);
		}
	}

}
